import React from "react";
import PropTypes from "prop-types";

// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import InputAdornment from "@material-ui/core/InputAdornment";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";

// @material-ui/icons
import Face from "@material-ui/icons/Face";
import Email from "@material-ui/icons/Email";
import Check from "@material-ui/icons/Check";

// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Card from "components/Card/Card.jsx";
import CardBody from "components/Card/CardBody.jsx";

import SCLogo from "assets/img/scapeshift/sc1.jpeg";
import registerPageStyle from "assets/jss/material-dashboard-pro-react/views/registerPageStyle";
import Switch from "@material-ui/core/Switch";

import Camera from 'react-html5-camera-photo';
import ReactCodeInput from 'react-verification-code-input';

import axios from 'axios';

class RegisterPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      surname: '',
      username: '',
      password: '',
      doc_number: '',
      phone: '',
      address: '',
      company: '',
      job: '',
      is_signatory: false,
      camera: false,
      dni: false,
      waitEmail: false,
      verifyEmail: false,
      checked: []
    };
    this.handleToggle = this.handleToggle.bind(this);
  }
  handleToggle(value) {
    const { checked } = this.state;
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    this.setState({
      checked: newChecked
    });
  }

  handleInput = (type, e) => {
    switch(type) {
      case 'name': this.setState({nameLast: e.target.value}); break;
      case 'surname': this.setState({nameLast: e.target.value}); break;
      case 'username': this.setState({username: e.target.value}); break;
      case 'password': this.setState({password: e.target.value}); break;
      case 'phone': this.setState({phone: e.target.value}); break;
      case 'address': this.setState({address: e.target.value}); break;
      case 'company': this.setState({business: e.target.value}); break;
      case 'job': this.setState({work: e.target.value}); break;
      case 'is_signatory': this.setState({rol: e.target.value}); break;
    }
  }

  testAPI = () => {
    const body = JSON.stringify({
      "username": this.state.username,
      "password": this.state.password,
      "name": this.state.name,
      "surname": this.state.surname,
      "phone": this.state.phone,
      "address": this.state.address,
      "company": this.state.company,
      "job": this.state.job,
      "is_signatory": true,
      "doc_number": this.state.doc_number
    })
    axios.post('http://172.17.102.25:8000/api/v1/users/create/', body, {
      headers: {
        'Content-Type': 'application/json',
      }
    })
    .then((response) => {
      console.log(response.data)
    })
  }

  handleChange = name => event => {
    this.setState({[name]: event.target.checked });
  };

  prevRegister = () => {this.setState({camera: false})}
  nextDni = () => { this.setState({camera: true})}

  onTakePhoto (dataUri) {
    // Do stuff with the dataUri photo...
    console.log('takePhoto');
  }

  signin = () => { 
    console.log('nameLast', this.state)
  }

  render() {
    const { classes } = this.props;
    const { camera } = this.state
    return (
      <div className={classes.container}>
        <GridContainer justify="center">
          <GridItem xs={12} sm={12} md={10}>
            <Card className={classes.cardSignup}>
